nta.declaration:
// GLOBAL VARIABLE
clock day_time;
const int END_OF_THE_DAY = 80;

//SIMPLE SETTINGS
//Train settings
const int NUM_TRAINS = 2;
const int RECHARGING_MULTIPLIER = 3;
const int SPEED[NUM_TRAINS] = {7, 8};
const int MAX_CHARGE[NUM_TRAINS] = {30, 28}; //time
const int INITIAL_CHARGE[NUM_TRAINS] ={28, 28}; //must be less than MAX_CHARGE
const int INITIAL_STATION[NUM_TRAINS] = {0, 2};
const int T_LINES[NUM_TRAINS] = {0, 0};
const int NEXT_STATION[NUM_TRAINS] = {1, 1}; //must be inside the line
bool FINISH[NUM_TRAINS] = {false, false};


//Station settings
const int NUM_STATIONS = 3;
const int SPARE_TIME = 6;
const int MAX_TRACKS[NUM_STATIONS] = {2,1,2};
const int PASSENGERS_TIME = 3;
const int REQUEST_DELAY = 2;
int TRAINS_INSIDE[NUM_STATIONS] = {1,0,1};
int WAITING_TRAINS [NUM_STATIONS] = {0,0,0};

//Railways settins
const int NUM_LINES = 2;
const int NUM_MAX_STATIONS_LINE = 3;
const int LINES[NUM_LINES][NUM_MAX_STATIONS_LINE] = {{0,1,2},{0,1,-1}}; //0,1,2
const int DISTANCES [NUM_STATIONS][NUM_STATIONS] ={{0,70,-1},
                                                   {70,0,50},
                                                   {-1,50,0}};
const int MAX_DELAYS [NUM_STATIONS][NUM_STATIONS] ={{0,60,-1},
                                                    {60,0,60},
                                                    {-1,60,0}};

